import React, { useState, useEffect } from 'react'
import blogService from './services/blogs'
import ShowBlogs from './components/showBlogs'

const App = () => {
  const [blogs, setBlogs] = useState([])

  useEffect(() => {
    blogService.getAll().then(blogs =>
      setBlogs( blogs )
    )  
  }, [])

  let user = null 

  return (
    <div>
      <h2>blogs</h2>
      {user !== null &&
      <ShowBlogs 
      blogs={blogs}/> 
      }
      {user === null &&
      <p>login</p>
      }
    </div>
  )
}

export default App
